bin/app.tslib/stacks/network-stack.tslib/stacks/database-stack.ts <pre><code>#!/usr/bin/env node
import 'source-map-support/register';
import * as cdk from 'aws-cdk-lib';
import { NetworkStack } from '../lib/stacks/network-stack';
import { DatabaseStack } from '../lib/stacks/database-stack';
import { ApplicationStack } from '../lib/stacks/application-stack';
import { prodConfig } from '../lib/config/prod';

const app = new cdk.App();
const environment = process.env.ENV || 'dev';

// 1. 基盤レイヤー (変更頻度: 低)
const networkStack = new NetworkStack(app, `${environment}-NetworkStack`, {
  env: prodConfig.env,
});

// 2. データレイヤー (変更頻度: 中)
const databaseStack = new DatabaseStack(app, `${environment}-DatabaseStack`, {
  env: prodConfig.env,
  vpc: networkStack.vpc,  // ネットワークスタックに依存
});

// 3. アプリケーションレイヤー (変更頻度: 高)
const applicationStack = new ApplicationStack(app, `${environment}-ApplicationStack`, {
  env: prodConfig.env,
  vpc: networkStack.vpc,
  database: databaseStack.database,  // データベーススタックに依存
});
</code></pre> <pre><code>import * as cdk from 'aws-cdk-lib';
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import { Construct } from 'constructs';

export class NetworkStack extends cdk.Stack {
  public readonly vpc: ec2.Vpc;

  constructor(scope: Construct, id: string, props?: cdk.StackProps) {
    super(scope, id, props);

    // VPCの作成
    this.vpc = new ec2.Vpc(this, 'MainVpc', {
      maxAzs: 3,
      natGateways: 1,
      subnetConfiguration: [
        {
          name: 'Public',
          subnetType: ec2.SubnetType.PUBLIC,
        },
        {
          name: 'Private',
          subnetType: ec2.SubnetType.PRIVATE_WITH_EGRESS,
        },
        {
          name: 'Isolated',
          subnetType: ec2.SubnetType.PRIVATE_ISOLATED,
        },
      ],
    });

    // Outputとしてエクスポート
    new cdk.CfnOutput(this, 'VpcId', {
      value: this.vpc.vpcId,
      exportName: `${this.stackName}-VpcId`,
    });
  }
}
</code></pre> <pre><code>import * as cdk from 'aws-cdk-lib';
import * as rds from 'aws-cdk-lib/aws-rds';
import * as ec2 from 'aws-cdk-lib/aws-ec2';
import { Construct } from 'constructs';

export interface DatabaseStackProps extends cdk.StackProps {
  vpc: ec2.IVpc;
}

export class DatabaseStack extends cdk.Stack {
  public readonly database: rds.DatabaseInstance;

  constructor(scope: Construct, id: string, props: DatabaseStackProps) {
    super(scope, id, props);

    // RDSインスタンスの作成
    this.database = new rds.DatabaseInstance(this, 'Database', {
      engine: rds.DatabaseInstanceEngine.postgres({
        version: rds.PostgresEngineVersion.VER_15,
      }),
      vpc: props.vpc,
      vpcSubnets: {
        subnetType: ec2.SubnetType.PRIVATE_ISOLATED,
      },
      instanceType: ec2.InstanceType.of(
        ec2.InstanceClass.T3,
        ec2.InstanceSize.MEDIUM
      ),
    });

    // Outputとしてエクスポート
    new cdk.CfnOutput(this, 'DatabaseEndpoint', {
      value: this.database.dbInstanceEndpointAddress,
      exportName: `${this.stackName}-Endpoint`,
    });
  }
}
</code></pre>
